mapper.update:
update message
set guest_id = #{guestId,jdbcType=INTEGER},
  title = #{title,jdbcType=VARCHAR},
  content = #{content,jdbcType=VARCHAR}
where message_id = #{messageId,jdbcType=INTEGER}

mapper.select:
select message_id, guest_id, title, content
from message
where message_id = #{messageId,jdbcType=INTEGER}
