mapper.update:
update news
set title = #{title,jdbcType=VARCHAR},
  show = #{show,jdbcType=VARCHAR},
  time = #{time,jdbcType=TIMESTAMP},
  delfig = #{delfig,jdbcType=VARCHAR}
where news_id = #{newsId,jdbcType=INTEGER}

mapper.select:
select news_id, title, show, time, delfig
from news
where news_id = #{newsId,jdbcType=INTEGER}

mapper.update:
update news
set delfig = #{delfig,jdbcType=VARCHAR}
where news_id = #{newsId,jdbcType=INTEGER}
